mapper.select:

where delete_flag='1' and app_code=${appCode}
and ${model.condition}
order by ${model.orderField} ${model.orderType} limit ${model.pageIndex},${model.pageSize}

select count(*) from cssd_dterminfo
where delete_flag='1' and app_code=#{appCode}
and ${condition}

mapper.update:
update cssd_dterminfo
term_no = #{info.termNo}, term_name = #{info.termName}, term_days = #{info.termDays}, order_no = #{info.orderNo}, create_by = #{info.createBy}, create_time = #{info.createTime}, update_by = #{info.updateBy}, update_time = #{info.updateTime}, delete_flag = #{info.deleteFlag}, comments = #{info.comments}, app_code = #{info.appCode}, version = #{info.version}
where delete_flag='1' and app_code=#{info.appCode} and version=#{info.version} and term_no = #{info.termNo}

mapper.delete:
delete from cssd_dterminfo
where delete_flag='1' and app_code=#{appCode} and term_no in

    #{no}

delete from cssd_dterminfo
where delete_flag='1' and app_code=#{appCode}
and ${condition}

update cssd_dterminfo set delete_flag='0'
where delete_flag='1' and app_code=#{appCode} and term_no in

    #{no}

update cssd_dterminfo set delete_flag='0'
where delete_flag='1' and app_code=#{appCode}
and ${condition}
