mapper.select:

where delete_flag='1' and app_code=${appCode}
and ${model.condition}
order by ${model.orderField} ${model.orderType} limit ${model.pageIndex},${model.pageSize}

select count(*) from cssd_packclass
where delete_flag='1' and app_code=#{appCode}
and ${condition}

mapper.update:
update cssd_packclass
packe_no = #{info.packeNo}, packe_name = #{info.packeName}, order_no = #{info.orderNo}, branch_no = #{info.branchNo}, create_by = #{info.createBy}, create_time = #{info.createTime}, update_by = #{info.updateBy}, update_time = #{info.updateTime}, delete_flag = #{info.deleteFlag}, comments = #{info.comments}, app_code = #{info.appCode}, version = #{info.version}
where delete_flag='1' and app_code=#{info.appCode} and version=#{info.version} and packe_no = #{info.packeNo}

mapper.delete:
delete from cssd_packclass
where delete_flag='1' and app_code=#{appCode} and packe_no in

    #{no}

delete from cssd_packclass
where delete_flag='1' and app_code=#{appCode}
and ${condition}

update cssd_packclass set delete_flag='0'
where delete_flag='1' and app_code=#{appCode} and packe_no in

    #{no}

update cssd_packclass set delete_flag='0'
where delete_flag='1' and app_code=#{appCode}
and ${condition}
