mapper.select:

where delete_flag='1' and app_code=${appCode}
and ${model.condition}
order by ${model.orderField} ${model.orderType} limit ${model.pageIndex},${model.pageSize}

select count(*) from sys_rotaclass
where delete_flag='1' and app_code=#{appCode}
and ${condition}

mapper.update:
update sys_rotaclass
class_no = #{info.classNo}, class_name = #{info.className}, order_no = #{info.orderNo}, branch_no = #{info.branchNo}, create_by = #{info.createBy}, create_time = #{info.createTime}, update_by = #{info.updateBy}, update_time = #{info.updateTime}, delete_flag = #{info.deleteFlag}, comments = #{info.comments}, app_code = #{info.appCode}, version = #{info.version}
where delete_flag='1' and app_code=#{info.appCode} and version=#{info.version} and class_no = #{info.classNo}

mapper.delete:
delete from sys_rotaclass
where delete_flag='1' and app_code=#{appCode} and class_no in

    #{no}

delete from sys_rotaclass
where delete_flag='1' and app_code=#{appCode}
and ${condition}

update sys_rotaclass set delete_flag='0'
where delete_flag='1' and app_code=#{appCode} and class_no in

    #{no}

update sys_rotaclass set delete_flag='0'
where delete_flag='1' and app_code=#{appCode}
and ${condition}
